CREATE TABLE "comic" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "folder_name" varchar NOT NULL UNIQUE DEFAULT (''), "title" varchar NOT NULL DEFAULT (''), "year" varchar NOT NULL DEFAULT (''), "image" varchar NOT NULL DEFAULT (''), "comicVineId" integer NOT NULL DEFAULT (''), "description" varchar NOT NULL DEFAULT (''), "api_detail_url" varchar NOT NULL DEFAULT (''), "site_detail_url" varchar NOT NULL DEFAULT (''), "finished" boolean NOT NULL DEFAULT (0), "publisherId" integer, FOREIGN KEY("publisherId") REFERENCES "publisher"("id"))
CREATE TABLE "config" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "comicsPath" varchar NOT NULL DEFAULT (''), "comicVineAPI" varchar NOT NULL DEFAULT (''))
CREATE TABLE "issue" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "folder_name" varchar NOT NULL DEFAULT (''), "file_name" varchar NOT NULL DEFAULT (''), "title" varchar NOT NULL DEFAULT (''), "year" varchar NOT NULL DEFAULT (''), "image" varchar NOT NULL DEFAULT (''), "comicVineId" integer NOT NULL DEFAULT (0), "number" integer NOT NULL DEFAULT (0), "possessed" boolean NOT NULL DEFAULT (0), "api_detail_url" varchar NOT NULL DEFAULT (''), "site_detail_url" varchar NOT NULL DEFAULT (''), "annual" boolean NOT NULL DEFAULT (0), "date" date, "comicId" integer, "readingStatusId" integer, FOREIGN KEY("comicId") REFERENCES "comic"("id"), FOREIGN KEY("readingStatusId") REFERENCES "reading_status"("id"))
CREATE TABLE "publisher" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "api_detail_url" varchar NOT NULL DEFAULT (''), "comicVineId" integer NOT NULL UNIQUE DEFAULT (0), "name" varchar NOT NULL UNIQUE DEFAULT (''))
CREATE TABLE "reading_status" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "read" boolean NOT NULL DEFAULT (0), "pageCount" integer NOT NULL DEFAULT (0), "currentPage" integer NOT NULL DEFAULT (0))
